/**
 * @description Runs a build for a site. The build will be scheduled to run at the first opportunity, but it might not start immediately if insufficient account build capacity is available.
 * Files for build can be uploaded as a zipped site using one of these methods:
 * 1. Set Content-Type to 'application/zip' and send the zip file as the raw request body
 * 2. Set Content-Type to 'multipart/form-data' and include the zip file in the 'zip' field
 * {@link /sites/:site_id/builds}
 */
export function createSiteBuild({
	pathParams: { site_id },
	queryParams,
}: {
	pathParams: CreateSiteBuildPathParams;
	queryParams?: CreateSiteBuildQueryParams;
}): Effect.Effect<
	CreateSiteBuildMutationResponse,
	ApiError | ValidationError | CreateSiteBuild400 | CreateSiteBuild404 | CreateSiteBuild422,
	ApiClient
> {
	return Effect.gen(function* () {
		const client = yield* ApiClient;

		if (site_id === undefined) {
			return yield* Effect.fail(
				new ValidationError({
					field: "site_id",
					reason: "Missing required path parameter",
				}),
			);
		}

		const url = `/sites/${site_id}/builds`;
		const searchParams = serializeQueryParams(queryParams);
		const fullUrl = url + (searchParams ? "?" + searchParams : "");

		const formData = new FormData();
		if (body) {
			Object.entries(body).forEach(([key, value]) => {
				if (value instanceof File || value instanceof Blob || typeof value === "string") {
					formData.append(key, value);
				} else if (value !== undefined && value !== null) {
					formData.append(key, String(value));
				}
			});
		}

		const response = yield* client.request({
			method: "POST",
			url: fullUrl,

			headers: { "Content-Type": "multipart/form-data" },
		});

		return response as CreateSiteBuildMutationResponse;
	});
}

